SELECT TPCDS.STORE_SALES.SS_WHOLESALE_COST , TPCDS.STORE_RETURNS.SR_NET_LOSS FROM TPCDS.STORE_SALES INNER JOIN TPCDS.STORE_RETURNS ON TPCDS.STORE_RETURNS.SR_TICKET_NUMBER = TPCDS.STORE_SALES.SS_TICKET_NUMBER AND TPCDS.STORE_RETURNS.SR_ITEM_SK = TPCDS.STORE_SALES.SS_ITEM_SK WHERE TPCDS.STORE_SALES.SS_STORE_SK = 2 AND TPCDS.STORE_RETURNS.SR_RETURNED_DATE_SK = 2451680;

SELECT TPCDS.CATALOG_RETURNS.CR_RETURNING_ADDR_SK , TPCDS.CATALOG_SALES.CS_SALES_PRICE FROM TPCDS.CATALOG_RETURNS INNER JOIN TPCDS.CATALOG_SALES ON TPCDS.CATALOG_SALES.CS_ORDER_NUMBER = TPCDS.CATALOG_RETURNS.CR_ORDER_NUMBER AND TPCDS.CATALOG_SALES.CS_ITEM_SK = TPCDS.CATALOG_RETURNS.CR_ITEM_SK WHERE TPCDS.CATALOG_SALES.CS_NET_PAID <= +00456.68 AND TPCDS.CATALOG_RETURNS.CR_REFUNDED_HDEMO_SK <= 939

SELECT TPCDS.STORE_SALES.SS_EXT_LIST_PRICE , TPCDS.STORE_RETURNS.SR_STORE_CREDIT FROM TPCDS.STORE_SALES INNER JOIN TPCDS.STORE_RETURNS ON TPCDS.STORE_RETURNS.SR_CUSTOMER_SK = TPCDS.STORE_SALES.SS_CUSTOMER_SK AND TPCDS.STORE_RETURNS.SR_ITEM_SK = TPCDS.STORE_SALES.SS_ITEM_SK WHERE TPCDS.STORE_SALES.SS_ADDR_SK >= 8970 AND TPCDS.STORE_RETURNS.SR_CUSTOMER_SK <= 93014

SELECT SR_ADDR_SK, SR_CDEMO_SK, SR_RETURN_AMT, SR_RETURN_AMT_INC_TAX, SR_RETURN_TIME_SK, SR_REVERSED_CHARGE, SR_STORE_CREDIT, SS_EXT_DISCOUNT_AMT, SS_SOLD_DATE_SK, SS_LIST_PRICE, SS_QUANTITY, SS_SALES_PRICE, SS_SOLD_TIME_SK, SS_WHOLESALE_COST FROM TPCDS.STORE_RETURNS JOIN TPCDS.STORE_SALES ON SR_ITEM_SK = SS_ITEM_SK AND SR_CUSTOMER_SK = SS_CUSTOMER_SK AND SS_TICKET_NUMBER = SR_TICKET_NUMBER
